import { Component } from 'react';
import './App.css';

class App extends Component {
  state = { person:{
    fullName:'ahmed',
    bio:'no comment' ,
    imgSrc:Img,
    profession:'FS Web Developer'
  }
 } 
 componentDidMount=()=>{
  this.intervalID = setInterval(
  ()=>{
    this.setState({
      timeInterval : this.state.timeInterval +1
    })
  }  
  )
 }
 toggleShow=()=>{
  this.setState({
    shows:!this.state.shows
  },1000)
  componentWillMount=()=>{
    clearInterval(this.intervalID)
  }
 }
  render() { 
    return (
    <>
        <button onClick={this.toggleShow}>show profile</button>  
        this.state.shows && <div>
          <h1>{this.state.person.fullName}</h1>
          <p>{this.state.person.bio}</p>
          <img src={this.state.person.imgSrc} alt='' />
          <p>{this.state.person.profession}</p></div>

          </>
    );
  }
}


export default App;
